command:
// Replace the default musket with a fast loading musket
const actor = arguments.length > 0 ? arguments[1] : null;

if (!actor) return;

if (!actor.items.find(i => i.name === 'Fast Reloading')) return;

const musket = actor.items.find(i => i.name === 'Musket');
if (!musket) return;

await musket.updateData('description', musket.description.replaceAll(/ 2 actions/g, ' 1 action'));
await musket.updateData('reloadTime', 1); 
return;

await actor.updateEmbeddedDocuments('Item', [{
 '_id': musket.id,
 'data.description': musket.description.replaceAll(/ 2 actions/g, ' 1 action'),
 'data.reloadTime': 1,
}]);
