// Check if module is installed and active
if (!game.modules.get('pirate-borg-premium')?.active) {
  ui.notifications.warn('The Ash Experience Generator table requires the "Pirate Borg Premium" module.');
  return;
}

// Get the table by UUID
const tableUuid = 'Compendium.pirate-borg-premium.rolltables.RollTable.9m3fmkKYEnWREhxX';
const table = await fromUuid(tableUuid);

if (!table) {
  ui.notifications.error('Could not find Ash Experience Generator table.');
  return;
}

// Roll on the table (GM only - blind roll)
await table.draw({ 
  displayChat: true, 
  rollMode: CONST.DICE_ROLL_MODES.BLIND 
});

