const hasItem = actor.items.some(item => item.name === 'Black Powder Poet');
if (hasItem) {
 const table = await game.pirateborg.api.compendium.findCompendiumItem('pirateborg.rolls-character-creation', 'd6 Grenades & Bombs');
 const total = (await game.pirateborg.api.utils.evaluateFormula('d4')).total;
 const draw = await table.drawMany(total, { displayChat: false });
 const items = await game.pirateborg.api.compendium.findTableItems(draw.results);
 await actor.createEmbeddedDocuments('Item', items.map(item => item.toObject(false))); 
}
